            let _token = '{{csrf_token()}}'

            P.postAjax('{{ route('p3_add_customer') }}', {_token, name, phone, address, quantity, description}, function (response) {
                console.log(response)
                window.location = `{{ route('frontend.p3.thanks') }}`
            });

